<!-- Page script -->
<script>
    $(document).ready(function() {
     $('#btn_cancel').click(function() {
         swal({
                 title: "Are you sure?",
                 text: "the data that you have created will be in the exhaust!",
                 type: "warning",
                 showCancelButton: true,
                 confirmButtonColor: "#DD6B55",
                 confirmButtonText: "Yes!",
                 cancelButtonText: "No!",
                 closeOnConfirm: true,
                 closeOnCancel: true
             },
             function(isConfirm) {
                 if (isConfirm) {
                     window.location.href = HTTP_REFERER;
                 }
             });

         return false;
     }); /*end btn cancel*/
     $('.btn_save').click(function() {
         $('.message').fadeOut();
         var form_menu_type = $('#form_menu_type');
         var data_post = form_menu_type.serializeArray();
         var save_type = $(this).attr('data-stype');

         data_post.push({
             name: 'save_type',
             value: save_type
         });

         $('.loading').show();

         $.ajax({
                 url: BASE_URL + '/menu_type/add_save',
                 type: 'POST',
                 dataType: 'json',
                 data: data_post,
             })
             .done(function(res) {
                 if (res.success) {
                     var id = $('#menu_type_avatar_galery').find('li').attr('qq-file-id');

                     if (save_type == 'back') {
                         window.location.href = res.redirect
                         return;
                     }

                     $('.message').printMessage({
                         message: res.message
                     });

                     $('.message').fadeIn();

                     $('form input[type != hidden], form textarea, form select').val('');

                 } else {

                     $('.message').printMessage({
                         message: res.message,
                         type: 'warning'
                     });

                     $('.message').fadeIn();
                 }
             })
             .fail(function() {
                 $('.message').printMessage({
                     message: 'Gagal menyimpan data',
                     type: 'warning'
                 });

             })
             .always(function() {
                 $('.loading').hide();
                 $('html, body').animate({
                     scrollTop: $(document).height()
                 }, 1000);
             });
         return false;

     }); /*end btn save*/
 }); /*end doc ready*/
</script>